import React, { useContext, useState } from 'react';
import {
	BrowserRouter as Router,
	Switch,
	Route,
	Link,
	Redirect,
	useParams,
	useHistory,
	useRouteMatch
} from 'react-router-dom';
import Login from './shared/Login';
import NavBar from './shared/NavBar';
import UserPage from './shared/UserPage';

type AuthContextType = {
	authenticated: boolean;
	userId: number;
	signIn: (userId: number) => void,
	signOut: () => void
};

export const AuthContext = React.createContext<AuthContextType>({
	authenticated: false,
	userId: 0,
	signIn: () => console.log("Sign in"),
	signOut: () => console.log("Sign out"),
});

function App() {
	const signIn = (userId: number) => {
		setAuthState({...authState, authenticated: true, userId: userId})
	}
	const signOut = () => {
		setAuthState({...authState, authenticated: true, userId: 0})
	}
	const [authState, setAuthState] = useState({
		authenticated: false,
		userId: 0,
		signIn: signIn,
		signOut: signOut
	});
	return (
		<AuthContext.Provider value={authState}>
			<div className="App h-screen w-screen flex justify-center items-center bg-purple-800">
				<Router>
					<Switch>
						<Route path="/u/:userId">
							<UserPage />
						</Route>
						<Route path="/">
							<Login />
						</Route>
					</Switch>
				</Router>
			</div>
		</AuthContext.Provider>
	);
}

export default App;
