import { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { ActionButton, Input } from '../styled/Components';

const ResetPassword = (props: any) => {
	const [stage, setStage] = useState("email");
	const history = useHistory();

	let element = <div></div>;
	if (stage === "email") {
		element = <EmailStage nextStage={() => setStage("code")} />
	} else if (stage === "code") {
		element = <Code nextStage={() => setStage("password")} />
	} else if (stage === "password") {
		element = <NewPassword nextStage={() => setStage("done")} />
	} else {
		history.push("/");
	}

	return element;
}

const EmailStage = (props: { nextStage: () => void }) => {
	return (
		<div className="flex flex-col justify-around items-center bg-gray-200 w-1/2 h-1/2 rounded-xl p-10">
			<p className="text-indigo-700 text-2xl border-b-2 border-gray-600 w-full">Enter your email</p>
			<Input type="text" className="my-5" name="email">
			</Input>
			<ActionButton onClick={props.nextStage} className="w-1/3 p-3">Continue</ActionButton>
		</div>
	);
}

const Code = (props: { nextStage: () => void }) => {
	return (
		<div className="flex flex-col bg-gray-200">
			Enter code
			<ActionButton onClick={props.nextStage}>Go to next stage</ActionButton>
		</div>
	);
}

const NewPassword = (props: { nextStage: () => void }) => {
	return (
		<div className="flex flex-col bg-gray-200">
			Enter new password
			<ActionButton onClick={props.nextStage}>Go to next stage</ActionButton>
		</div>
	);
}

export default ResetPassword;
