import { useContext, useEffect, useState } from 'react';
import { ContentPage, ContentPageContext, Popup, Label, Input } from '../styled/Components';
import { AuthContext } from '../App';
import apiLink from '../API';
import { useRouteMatch, useParams } from 'react-router-dom';
import { User } from '../types/Common';

const UserList = (props: any) => {
	const { userId } = useParams() as any;
	const authContext = useContext(AuthContext);
	const contentPageContext = useContext(ContentPageContext);
	const { url } = useRouteMatch();

	useEffect(() => {
		contentPageContext.setLocationList([{ title: "User list", url: url }]);
	}, []);

	const [users, setUsers] = useState<User[]>([]);
	useEffect(() => {
		const bearer = "Bearer " + authContext.jwtToken;
		console.log(bearer);
		fetch(`${apiLink}/admin/${userId}/users`, {
			headers: {
				'Authorization': bearer
			}
		})
			.then(res => res.json())
			.then(res => {
				if (res.status === "OK") {
					setUsers(res.result);
					console.log(res);
				} else {
					alert(res.message);
				}
			}).catch(err => console.log(err));
	}, []);

	return (
		<div className="mx-10 border-l-2 border-r-2 border-b-2 border-gray-800">
			<div className="bg-gray-800 flex flex-row justify-center w-full text-gray-300 p-2">
				<p className="font-bold flex-1 text-center">Type</p>
				<p className="font-bold flex-1 text-center">Email</p>
				<p className="font-bold flex-1 text-center">Firstname</p>
				<p className="font-bold flex-1 text-center">Lastname</p>
				<p className="font-bold flex-1 text-center">Supervisor</p>
			</div>
			{users.map(user => <UserItem user={user} />)}
		</div>
	);
}

const UserItem = (props: { user: User }) => {
	const { user } = props;
	const [popupShown, setPopupShown] = useState(false);
	return (
		<>
			<div onClick={() => setPopupShown(true)}
				className="flex flex-row justify-center w-full hover:bg-gray-400 p-5 border-b border-gray-400">
				<p className="flex-1 text-center">{user.type}</p>
				<p className="flex-1 text-center">{user.email}</p>
				<p className="flex-1 text-center">{user.firstname}</p>
				<p className="flex-1 text-center">{user.lastname}</p>
				<p className="flex-1 text-center">{user.supervisor?.firstname}</p>
			</div>
			{popupShown ? <UserView user={user} close={() => setPopupShown(false)} /> : null}
		</>
	);
}

const UserView = (props: { user: User, close: () => void }) => {
	const { user, close } = props;
	const [form, setForm] = useState<User>(user);
	const onChange = (event: React.ChangeEvent) => {
		event.preventDefault();
		const { name, value } = event.target as any;
		setForm({ ...form, [name]: value });
	}
	return (
		<Popup>
			<div className="flex flex-col w-1/2 min-w-min min-h-screen m-auto bg-gray-200 p-5 text-gray-800">

				<div className="flex flex-row justify-center">
					<p className="font-bold flex-1 text-lg text-center">{`${user.firstname} ${user.lastname}`}</p>
					<i onClick={props.close} className="material-icons cursor-pointer text-gray-600 hover:text-gray-800">close</i>
				</div>

				<form>
					<Label>
						<p>Email</p>
						<Input type="text" name="email" onChange={onChange} value={form.email}></Input>
					</Label>
					<Label>
						<p>Firstname</p>
						<Input type="text" name="firstname" onChange={onChange} value={form.firstname}></Input>
					</Label>
					<Label>
						<p>Lastname</p>
						<Input type="text" name="lastname" onChange={onChange} value={form.lastname}></Input>
					</Label>
					<Label>
						<p>Type</p>
						<Input type="text" name="type" onChange={onChange} value={form.type}></Input>
					</Label>
					<Label>
						<p>Supervisor</p>
						<Input type="number" name="supervisor" onChange={onChange} value={form.supervisor?.id}></Input>
					</Label>
				</form>

			</div>
		</Popup>
	);
}

export default UserList;
