import { useContext, useEffect, useState } from 'react';
import { ContentPage, ContentPageContext, Popup, Label, Input, Select, ActionButton, Form, DangerButton } from '../styled/Components';
import { AuthContext } from '../App';
import apiLink from '../API';
import { useRouteMatch, useParams } from 'react-router-dom';
import { User } from '../types/Common';

const UserList = (props: any) => {
	const { userId } = useParams() as any;
	const authContext = useContext(AuthContext);
	const contentPageContext = useContext(ContentPageContext);
	const { url } = useRouteMatch();

	useEffect(() => {
		contentPageContext.setLocationList([{ title: "User list", url: url }]);
	}, []);

	const [users, setUsers] = useState<User[]>([]);
	const supervisors = users.filter(user => user.type === "supervisor");
	useEffect(() => {
		const bearer = "Bearer " + authContext.jwtToken;
		console.log(bearer);
		fetch(`${apiLink}/admin/${userId}/users`, {
			headers: {
				'Authorization': bearer
			}
		})
			.then(res => res.json())
			.then(res => {
				if (res.status === "OK") {
					setUsers(res.result);
					console.log(res);
				} else {
					alert(res.message);
				}
			}).catch(err => console.log(err));
	}, []);

	return (
		<div className="mx-10 border-l-2 border-r-2 border-b-2 border-gray-800">
			<div className="bg-gray-800 flex flex-row justify-center w-full text-gray-300 p-2">
				<p className="font-bold flex-1 text-center">Type</p>
				<p className="font-bold flex-1 text-center">Email</p>
				<p className="font-bold flex-1 text-center">Firstname</p>
				<p className="font-bold flex-1 text-center">Lastname</p>
				<p className="font-bold flex-1 text-center">Supervisor</p>
			</div>
			{users.map(user => <UserItem user={user} supervisors={supervisors} />)}
		</div>
	);
}

const UserItem = (props: { user: User, supervisors: User[] }) => {
	const { user, supervisors } = props;
	const [popupShown, setPopupShown] = useState(false);
	return (
		<>
			<div onClick={() => setPopupShown(true)}
				className="flex flex-row justify-center w-full hover:bg-gray-400 p-5 border-b border-gray-400">
				<p className="flex-1 text-center">{user.type}</p>
				<p className="flex-1 text-center">{user.email}</p>
				<p className="flex-1 text-center">{user.firstname}</p>
				<p className="flex-1 text-center">{user.lastname}</p>
				<p className="flex-1 text-center">{user.supervisor?.firstname}</p>
			</div>
			{popupShown ? <UserView user={user} close={() => setPopupShown(false)} supervisors={supervisors} /> : null}
		</>
	);
}

const UserView = (props: { supervisors: User[], user: User, close: () => void }) => {
	const authContext = useContext(AuthContext);
	const { user, close, supervisors } = props;

	const [form, setForm] = useState<User>({...user});

	const onChange = (event: React.ChangeEvent) => {
		event.preventDefault();
		const { name, value } = event.target as any;

		if (name === "type") {
			if (value === "admin" || value === "supervisor") {
				setForm({ ...form, [name]: value, supervisor: null });
				return;
			}
		}

		setForm({ ...form, [name]: value });
	}

	const changeSupervisor = async (event: React.ChangeEvent) => {
		event.preventDefault();
		const { name, value } = event.target as any;
		if(value == "0") {
			setForm({...form, supervisor: null});
			return;
		}

		for (let i = 0; i < supervisors.length; i++) {
			console.log({supervisor: supervisors[i], value: value, id: supervisors[i].id});
			if (supervisors[i].id == value) {
				console.log(true);
				setForm({ ...form, supervisor: supervisors[i] });
			}
		}
		console.log(form);
	}

	const onSubmit = async (event: React.FormEvent) => {
		event.preventDefault();
		const bearer = "Bearer " + authContext.jwtToken;
		console.log(form);
		await fetch(`${apiLink}/admin/${authContext.userId}/users/${user.id}`, {
			method: "put",
			headers: {
				"Content-Type": "application/json",
				"Authorization": bearer
			},
			body: JSON.stringify(form)
		})
			.then(res => res.json())
			.then(res => {
				if (res.status === "OK") {
					alert(res.message);
					console.log(res);
				} else {
					alert(res.message);
				}
			}).catch(err => console.log(err));
	}

	const onDelete = async (event: React.FormEvent) => {
		event.preventDefault();
	}

	return (
		<Popup>
			<div className="flex flex-col items-stretch w-1/2 min-w-min min-h-screen m-auto bg-gray-200 p-5 text-gray-800">

				<div className="flex flex-row justify-center">
					<p className="font-bold flex-1 text-lg text-center">{`${user.firstname} ${user.lastname}`}</p>
					<i onClick={props.close} className="material-icons cursor-pointer text-gray-600 hover:text-gray-800">close</i>
				</div>

				<Form onSubmit={onSubmit}>
					<Label>
						<p>Email</p>
						<Input type="text" name="email" onChange={onChange} value={form.email}></Input>
					</Label>
					<Label>
						<p>Firstname</p>
						<Input type="text" name="firstname" onChange={onChange} value={form.firstname}></Input>
					</Label>
					<Label>
						<p>Lastname</p>
						<Input type="text" name="lastname" onChange={onChange} value={form.lastname}></Input>
					</Label>
					<Label>
						<p>Type</p>
						<Input type="text" name="type" onChange={onChange} value={form.type}></Input>
					</Label>
					<Label>
						<p>Supervisor</p>
						<Select
							disabled={form.type === "supervisor" || form.type === "admin"}
							name="supervisor" onChange={changeSupervisor}
							value={form.supervisor == null ? 0 : form.supervisor.id}>

							<option value={0}>{"No supervisor"}</option>
							{supervisors.map(supervisor =>
								<option value={supervisor.id}>{`${supervisor.firstname} ${supervisor.lastname}`}</option>)
							}
						</Select>
					</Label>
					<ActionButton className="p-3 mt-3 w-40">Update</ActionButton>
					<DangerButton onClick={onDelete} className="p-3 mt-3 w-40">Delete</DangerButton>
				</Form>

			</div>
		</Popup>
	);
}

export default UserList;
