import { useContext, useEffect, useState } from 'react';
import { ContentPage, ContentPageContext, Popup, Label, Input, Select, ActionButton, Form, DangerButton } from '../styled/Components';
import { AuthContext } from '../App';
import { formatDate } from '../util/Utils';
import apiLink from '../API';
import { useRouteMatch, useParams } from 'react-router-dom';
import { Request, User } from '../types/Common';

/** 
* Displays the user list page
* @function RequestList
* */
const RequestList = (props: any) => {
	const { userId } = useParams() as any;
	const authContext = useContext(AuthContext);
	const { url } = useRouteMatch();

	/* Set the location list in the header of the page */
	const contentPageContext = useContext(ContentPageContext);
	useEffect(() => {
		contentPageContext.setLocationList([{ title: "Request list", url: url }]);
	}, []);

	/* Set up the state that holds the requests, and also a value for updating the
	* list with a callback*/
	const [requests, setRequests] = useState<Request[]>([]);
	const [shouldUpdate, setShouldUpdate] = useState(0);
	useEffect(() => {
		const bearer = "Bearer " + authContext.jwtToken;
		fetch(`${apiLink}/supervisor/${userId}/requests`, {
			headers: {
				'Authorization': bearer
			}
		})
			.then(res => res.json())
			.then(res => {
				if (res.status === "OK") {
					const usrs = res.result as Request[];
					setRequests(usrs.filter(usr => usr.id != authContext.userId));
				} else {
					alert(res.message);
				}
			}).catch(err => console.log(err));
	}, [userId, shouldUpdate]);

	return (
		<div className="flex flex-col">


			<div className="mx-10 border-l-2 border-r-2 border-b-2 border-gray-800">
				<div className="bg-gray-800 flex flex-row justify-center w-full text-gray-300 p-2">
					<p className="font-bold flex-1 text-center">Type</p>
					<p className="font-bold flex-1 text-center">Email</p>
					<p className="font-bold flex-1 text-center">Firstname</p>
					<p className="font-bold flex-1 text-center">Lastname</p>
					<p className="font-bold flex-1 text-center">Supervisor</p>
				</div>
			</div>
		</div>
	);
}

/** 
* Displays a request list item
* @function RequestItem
* @param {Object} props - The props of the component
* @param {Request} props.request - The request this item represents
* @param {()=>void} props.update - Callback for updating the main list
* @returns A single request item for the request list
* */
const RequestItem = (props: { request: Request, update: () => void }) => {
	const { request } = props;

	/* The state which determines whether the popup window to modify a user is
	* shown */
	const [popupShown, setPopupShown] = useState(false);
	return (
		<div onClick={() => setPopupShown(true)}
			className="flex flex-row justify-center w-full hover:bg-gray-400 p-5 border-b border-gray-400">
			<p className="flex-1 text-center">{request.startDate}</p>
			<p className="flex-1 text-center">{request.endDate}</p>
			<p className="flex-1 text-center">{request.user.firstname}</p>
			<p className="flex-1 text-center">{request.approved}</p>
		</div>
	);
}



export default RequestList;
